import { Suspense, useSyncExternalStore } from 'react';
import { createCachedResourceGetter } from './cache';
import CompoundComponent from './CompoundComponent';
import ErrorBoundary from './ErrorBoundary';

class ResourceManager {
  resource;
  promise;
  query;
  listeners = [];

  constructor(promise, query) {
    const createResource = createCachedResourceGetter(promise);

    this.resource = createResource(query);
    this.promise = promise;
    this.query = query;
  }

  subscribe(listener) {
    this.listeners = [...this.listeners, listener];

    return () => {
      this.listeners = this.listeners.filter((l) => l !== listener);
    };
  }

  private emitChange() {
    for (let listener of this.listeners) {
      listener();
    }
  }

  getResource() {
    return this.resource;
  }

  updateResource(newQuery = null) {
    const createResource = createCachedResourceGetter(this.promise);

    let queryToUse = this.query;
    if (newQuery) {
      queryToUse = newQuery;
    }

    this.resource = createResource(queryToUse);
    this.emitChange();
  }
}

const useResource = (promise, query) => {
  const resourceStore = new ResourceManager(promise, query);

  const getSnapshot = () => resourceStore.getResource();
  const subscribe = (listener) => resourceStore.subscribe(listener);

  const resource = useSyncExternalStore(subscribe, getSnapshot);

  return {
    resource,
    updateResource: (newQuery) => updateResource(promise, newQuery),
  };
};

const Suspender = ({ promise, query, children, loading = null, error = null }) => {
  const resourceProps = useResource(promise, query);

  return (
    <ErrorBoundary fallback={error}>
      <Suspense fallback={loading}>
        <CompoundComponent props={{ ...resourceProps }}>{children}</CompoundComponent>
      </Suspense>
    </ErrorBoundary>
  );
};

export default Suspender;
```

