const hasPermission = initializePermission(user);

const showAppTable = hasPermission('APPLICATION_TABLE');
const showTalentNetwork = hasPermission('TALENT_NETWORK_TABLE');
const showEmployerContent = hasPermission('EMPLOYER_CONTENT');
