const megaCache = [];

const createCachedResourceGetter = (callback) => {
  function getResource(id, invalidateCache = false) {
    let cache = megaCache.find((x) => x.callback === callback)?.cache;
    if (!cache) {
      megaCache.push({ callback, cache: [] });
      cache = [];
    }
    const cachedResource = cache.find((y) => y.id === id);
    if (!invalidateCache && cachedResource) {
      return cachedResource.resource;
    }
    // remove old cached resource
    if (cachedResource) {
      const idx = cache.indexOf(cachedResource);
      cache.splice(idx, 1);
    }
    const resource = suspendPromise(callback(id));
    cache.push({ id, resource });
    return resource;
  }
  return getResource;
};
```

